<script setup>
import { ref, onMounted, onUnmounted } from 'vue'

const onlineUserCount = ref(0)
const totalUserCount = ref(0)
const activeNav = ref('home')
const isDarkMode = ref(true) // Default to dark mode
const activeTab = ref(1)
let intervalId = null

// Initialize theme on component mount
onMounted(() => {
  console.log('=== Theme Initialization ===')
  
  const savedMode = localStorage.getItem('darkMode')
  console.log('savedMode from localStorage:', savedMode)
  
  if (savedMode !== null) {
    // User has a saved preference, use it
    isDarkMode.value = savedMode === 'true'
    console.log('Using saved user preference:', isDarkMode.value)
  } else {
    // No saved preference, default to dark mode
    isDarkMode.value = true
    console.log('No saved preference, defaulting to dark mode')
    // Save the default choice
    localStorage.setItem('darkMode', 'true')
  }
  
  // Apply initial theme
  updateDarkModeClass()
  
  console.log('Theme initialization complete')
})

function toggleDarkMode() {
  console.log('=== Theme Toggle Started ===')
  console.log('Current isDarkMode value:', isDarkMode.value)
  console.log('Current document classes:', document.documentElement.className)
  
  // Toggle the theme
  isDarkMode.value = !isDarkMode.value
  
  console.log('New isDarkMode value:', isDarkMode.value)
  console.log('Toggling to:', isDarkMode.value ? 'DARK' : 'LIGHT', 'mode')
  
  // Save the new preference
  localStorage.setItem('darkMode', isDarkMode.value.toString())
  console.log('Saved to localStorage:', localStorage.getItem('darkMode'))
  
  // Apply the theme
  updateDarkModeClass()
  
  console.log('=== Theme Toggle Complete ===')
}

function updateDarkModeClass() {
  console.log('=== Updating DOM Classes ===')
  console.log('isDarkMode.value:', isDarkMode.value)
  console.log('Document classes before update:', document.documentElement.className)
  
  // Force remove any existing dark class first
  document.documentElement.classList.remove('dark')
  
  if (isDarkMode.value) {
    console.log('Adding dark class to document.documentElement')
    document.documentElement.classList.add('dark')
  } else {
    console.log('Keeping document.documentElement in light mode (no dark class)')
  }
  
  console.log('Document classes after update:', document.documentElement.className)
  console.log('Has dark class:', document.documentElement.classList.contains('dark'))
  
  // Verify the change took effect
  setTimeout(() => {
    const hasDark = document.documentElement.classList.contains('dark')
    console.log('Verification - Document has dark class:', hasDark)
    console.log('Verification - Should match isDarkMode:', hasDark === isDarkMode.value)
  }, 100)
}

// Fetch online users function
const fetchOnlineUsers = async () => {
  try {
    const response = await fetch('https://new-api.bitcy.club/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
          query OnlineUsers {
            onlineUsers {
              id
            }
          }
        `
      })
    })

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`)
    }

    const data = await response.json()
    if (data.data?.onlineUsers) {
      onlineUserCount.value = data.data.onlineUsers.length || 0
    }
  } catch (error) {
    console.error('Failed to fetch online users:', error)
  }
}

const fetchTotalUsers = async () => {
  try {
    const response = await fetch('https://new-api.bitcy.club/graphql', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: `
          query AllUsersCountQuery {
          allUsersCountQuery
        }
        `
      })
    })

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`)
    }

    const data = await response.json()
    if (data.data?.allUsersCountQuery) {
      totalUserCount.value = data.data?.allUsersCountQuery || 0
    }
  } catch (error) {
    console.error('Failed to fetch total users:', error)
  }
}

// Initialize data fetching
onMounted(async () => {
  // Initial fetch
  await fetchOnlineUsers()
  await fetchTotalUsers()
  
  // Set up continuous polling every 5 seconds
  intervalId = setInterval(fetchOnlineUsers, 5000)
})

// Clean up interval when component is unmounted
onUnmounted(() => {
  if (intervalId) {
    clearInterval(intervalId)
  }
})
</script>

